import type { H3Event, AppOptions } from "h3";
import type { RenderResponse } from "nitropack/dist/runtime/renderer";
export type { NitroApp } from "nitropack/dist/runtime/app";
export type { CacheEntry, CacheOptions, ResponseCacheEntry, CachedEventHandlerOptions, } from "nitropack/dist/runtime/cache";
export type { NitroAppPlugin } from "nitropack/dist/runtime/plugin";
export type { RenderResponse, RenderHandler } from "nitropack/dist/runtime/renderer";
export type CapturedErrorContext = {
    event?: H3Event;
    [key: string]: unknown;
};
export type CaptureError = (error: Error, context: CapturedErrorContext) => void;
export interface NitroRuntimeHooks {
    close: () => void;
    error: CaptureError;
    request: NonNullable<AppOptions["onRequest"]>;
    beforeResponse: NonNullable<AppOptions["onBeforeResponse"]>;
    afterResponse: NonNullable<AppOptions["onAfterResponse"]>;
    "render:response": (response: Partial<RenderResponse>, context: {
        event: H3Event;
    }) => void;
}
